package Gen26;

public class CibiPreferiti {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] favoriteFood = {"pane", "carne", "pesce", "formiche", "coccodrilli"};
		
		System.out.println("favorite food" + favoriteFood[0]); // top food
		
		System.out.println("worst food" + favoriteFood[favoriteFood.length - 1]); // worst food
		
		System.out.println("middle food" + favoriteFood[favoriteFood.length / 2]); // middle food
		
		
	}

}
